mapper.select:
select a.*,p.name as patientname,d.name as doctorname,d.department as department from appointment a left join patient p on a.patientid=p.id left join doctor d on d.id=a.doctorid where 1=1
and a.patientid in
#{patientid}
and a.doctorid in
#{doctorid}
order by a.id

select a.*,p.name as patientname,d.name as doctorname from appointment a
left join patient p on a.patientid=p.id
left join doctor d on d.id=a.doctorid
where doctorid = #{doctorId}

  and a.patientid in

    #{patientid}



  and a.time=#{time}

order by a.id

mapper.update:
update appointment
set patientid = #{patientid,jdbcType=INTEGER},
  doctorid = #{doctorid,jdbcType=INTEGER},
  time = #{time,jdbcType=DATE}
where id = #{id,jdbcType=INTEGER}
