mapper.select:
select m.*,p.name as patientname,d.name as doctorname from medicalhistory m left join patient p on p.id=m.patientid left join doctor d on m.doctorid=d.id where 1=1
and m.patientid in
#{patientid}
and m.doctorid in
#{doctorid}
order by m.id

mapper.insert:
insert into medicalhistory (id, patientid, name,time,doctorid
  )
values (#{id,jdbcType=INTEGER}, #{patientid,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR},#{time},#{doctorid}
  )

mapper.update:
update medicalhistory
set patientid = #{patientid,jdbcType=INTEGER},
  name = #{name,jdbcType=VARCHAR},time=#{time},doctorid=#{doctorid}
where id = #{id,jdbcType=INTEGER}
